import React, { useState, useEffect } from "react";
import { Typography, Paper, TextField, Button, List, ListItem, ListItemText } from "@mui/material";
import RecipeSuggestions from "../RecipeSuggestions/RecipeSuggestions";
import "./Pantry.css";

const Pantry = () => {
  const [items, setItems] = useState([]); // Start with an empty array to prevent undefined
  const [newItem, setNewItem] = useState("");

  // Fetch items from the backend when the component mounts
  useEffect(() => {
    fetch("http://localhost:5000/pantry")
      .then((response) => response.json())
      .then((data) => {
        console.log("Fetched pantry items:", data); // Debug the fetched data
        setItems(data || []); // Ensure data is always an array
      })
      .catch((error) => console.error("Error fetching pantry items:", error));
  }, []);

  // Add a new item to the pantry and update the backend
  const addItem = () => {
    if (newItem.trim()) {
      const item = { name: newItem, category: "Uncategorized" };
      fetch("http://localhost:5000/pantry", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(item),
      })
        .then((response) => response.json())
        .then((newItem) => {
          console.log("New item added to pantry:", newItem);
          setItems([...items, newItem]);
        })
        .catch((error) => console.error("Error adding pantry item:", error));
      setNewItem(""); // Clear the input field after adding
    }
  };

  // Ensure that pantryItems is always an array
  const pantryItems = items.map((item) => item.name) || [];

  console.log("Rendering Pantry component with items:", items);
  console.log("Passing pantryItems to RecipeSuggestions:", pantryItems);

  return (
    <Paper className="pantry-container" elevation={3}>
      <Typography variant="h5" gutterBottom>
        Pantry
      </Typography>
      <div style={{ marginBottom: "20px" }}>
        <TextField
          variant="outlined"
          label="Add item to pantry"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          style={{ marginRight: "10px" }}
        />
        <Button variant="contained" color="primary" onClick={addItem}>
          Add
        </Button>
      </div>
      <Typography variant="h6">Current Pantry Items</Typography>
      <List>
        {items.map((item, index) => (
          <ListItem key={index}>
            <ListItemText primary={item.name} />
          </ListItem>
        ))}
      </List>
      {/* Pass the pantryItems array to RecipeSuggestions */}
      <RecipeSuggestions pantryItems={pantryItems} />
    </Paper>
  );
};

export default Pantry;
