import React, { useState } from "react";
import { Container, Typography, Grid, Paper } from "@mui/material";
import Profile from "./components/Profile/Profile";
import Pantry from "./components/Pantry/Pantry";
import RecipeSuggestions from "./components/RecipeSuggestions/RecipeSuggestions";
import ShoppingList from "./components/ShoppingList/ShoppingList";
import pantryLogo from "./assets/pantry-logo.png";
import "./App.css";

function App() {
  const [pantryItems, setPantryItems] = useState([]);
  const [dietPreferences, setDietPreferences] = useState({
    diet: "None",
    restrictions: "",
  });
  const [profile, setProfile] = useState({
    purpose: "Single Person",
    familyMembers: 1,
    cookDays: 0,
  });

  // Function to update pantry items
  const updatePantryItems = (items) => {
    setPantryItems(items);
  };

  // Function to update diet preferences
  const updateDietPreferences = (diet, restrictions) => {
    setDietPreferences({ diet, restrictions });
  };

  // Function to update profile information
  const updateProfile = (profileData) => {
    setProfile(profileData);
    console.log("Updated Profile:", profileData);
  };

  return (
    <Container maxWidth="lg" style={{ marginTop: "40px" }}>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "20px",
        }}
      >
        <img
          src={pantryLogo}
          alt="Pantry Logo"
          style={{ width: "60px", height: "60px", marginRight: "10px" }}
        />
        <Typography
          variant="h3"
          gutterBottom
          style={{
            fontFamily: "'Pacifico', cursive",
            color: "#4A4A4A",
            fontSize: "3rem",
          }}
        >
          Smart Pantry Manager
        </Typography>
      </div>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          {/* Profile Section */}
          <Profile
            updateProfile={updateProfile}
            updateDietPreferences={updateDietPreferences}
          />
        </Grid>

        <Grid item xs={12} md={6}>
          {/* Recipe Suggestions Section */}
          <Paper elevation={3} style={{ padding: "20px", marginBottom: "20px", minHeight: "270px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Recipe Suggestions
            </Typography>
            <RecipeSuggestions
              pantryItems={pantryItems}
              dietPreferences={dietPreferences}
            />
          </Paper>

          {/* Shopping List Section */}
          <Paper elevation={3} style={{ padding: "20px", minHeight: "300px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Shopping List
            </Typography>
            <ShoppingList
              pantryItems={pantryItems}
              dietPreferences={dietPreferences}
            />
          </Paper>
        </Grid>

        <Grid item xs={12} md={6}>
          {/* Pantry Management Section */}
          <Paper elevation={3} style={{ padding: "20px", minHeight: "300px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Pantry Management
            </Typography>
            <Pantry updatePantryItems={updatePantryItems} />
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
