import React, { useEffect, useState } from "react";
import { Typography, CircularProgress, Button } from "@mui/material";

const RecipeSuggestions = ({ pantryItems, dietPreferences }) => {
  const [recipes, setRecipes] = useState([]); // Store all the recipes
  const [currentRecipeIndex, setCurrentRecipeIndex] = useState(0); // Track current recipe
  const [loading, setLoading] = useState(false);

  // Refetch suggestions when pantry items or diet preferences change
  useEffect(() => {
    if (pantryItems.length > 0 || dietPreferences) {
      // Fetch only if there are pantry items or diet preferences
      setCurrentRecipeIndex(0); // Reset the recipe index
      fetchSuggestions(pantryItems, dietPreferences); 
    }
  }, [pantryItems, dietPreferences]);

  const fetchSuggestions = (pantryItems, dietPreferences) => {
    setLoading(true);
    setRecipes([]); // Clear previous recipes before fetching new ones

    fetch("http://127.0.0.1:5001/recommend", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        pantry: pantryItems,
        diet: dietPreferences.diet,
        restrictions: dietPreferences.restrictions,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        if (data.suggestions && data.suggestions.length > 0) {
          const formattedRecipes = formatRecipes(data.suggestions);
          setRecipes(formattedRecipes);
          setCurrentRecipeIndex(0); // Reset to the first recipe when new data is fetched
        } else {
          setRecipes([]);
        }
      })
      .catch((error) => {
        console.error("Error fetching recipe suggestions:", error);
        setRecipes([]);
      })
      .finally(() => {
        setLoading(false); // Stop loading after fetch completes
      });
  };

  const formatRecipes = (rawSuggestions) => {
    return rawSuggestions.map((suggestion) => {
      const colonIndex = suggestion.indexOf(":");
      if (colonIndex !== -1) {
        const title = suggestion.slice(0, colonIndex).trim();
        const description = suggestion.slice(colonIndex + 1).trim();
        return { title, description };
      }
      return { title: suggestion, description: "" }; // If no colon, treat the whole thing as a title
    });
  };

  const handleLike = () => {
    alert("You liked the recipe!");
  };

  const handleDislike = () => {
    if (currentRecipeIndex < recipes.length - 1) {
      setCurrentRecipeIndex(currentRecipeIndex + 1); // Show the next recipe
    } else {
      alert("No more recipes to show.");
    }
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <Typography variant="h5" gutterBottom>
        Recipe Suggestions from ChatGPT
      </Typography>

      {loading ? (
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          <CircularProgress />
        </div>
      ) : recipes.length > 0 ? (
        <div>
          {/* Display the current recipe */}
          {recipes[currentRecipeIndex] && (
            <>
              <Typography variant="h6" style={{ fontWeight: "bold", color: "#3f51b5" }}>
                {recipes[currentRecipeIndex].title} {/* Bold and styled title */}
              </Typography>
              <Typography variant="body1" style={{ whiteSpace: "pre-line", marginTop: "10px" }}>
                {recipes[currentRecipeIndex].description} {/* Instructions displayed normally */}
              </Typography>
            </>
          )}
          <div style={{ marginTop: "20px" }}>
            <Button
              variant="contained"
              color="primary"
              style={{ marginRight: "10px" }}
              onClick={handleLike}
            >
              Like
            </Button>
            <Button
              variant="contained"
              color="secondary"
              onClick={handleDislike}
            >
              Dislike
            </Button>
          </div>
        </div>
      ) : (
        <Typography>No recipe suggestions available.</Typography>
      )}
    </div>
  );
};

export default RecipeSuggestions;
