import React, { useState } from "react";
import { Container, Typography, Grid, Paper, AppBar, Toolbar, Button, Menu, MenuItem } from "@mui/material";
import Pantry from "./components/Pantry/Pantry";
import Profile from "./components/Profile/Profile";
import RecipeSuggestions from "./components/RecipeSuggestions/RecipeSuggestions";
import ShoppingList from "./components/ShoppingList/ShoppingList";
import "./App.css";
import pantryLogo from "./assets/pantry-logo.png";

function App() {
  const [pantryItems, setPantryItems] = useState([]);
  const [dietPreferences, setDietPreferences] = useState({ diet: "None", restrictions: "" });
  const [menuAnchor, setMenuAnchor] = useState(null);
  const [currentPage, setCurrentPage] = useState("Pantry Management"); // Default to Pantry Management

  const updatePantryItems = (items) => {
    setPantryItems(items);
  };

  const updateDietPreferences = (diet, restrictions) => {
    setDietPreferences({ diet, restrictions });
  };

  const handleMenuOpen = (event) => {
    setMenuAnchor(event.currentTarget);
  };

  const handleMenuClose = () => {
    setMenuAnchor(null);
  };

  const handleNavigation = (page) => {
    setCurrentPage(page);
    setMenuAnchor(null); // Close the menu after selecting an option
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case "Profile":
        return <Profile updateDietPreferences={updateDietPreferences} />;
      case "Shopping List":
        return <ShoppingList pantryItems={pantryItems} dietPreferences={dietPreferences} />;
      case "Pantry Management":
        return <Pantry updatePantryItems={updatePantryItems} />;
      case "Recipe Suggestions":
        return <RecipeSuggestions pantryItems={pantryItems} dietPreferences={dietPreferences} />;
      default:
        return <Pantry updatePantryItems={updatePantryItems} />;
    }
  };

  return (
    <Container maxWidth="lg" style={{ marginTop: "40px" }}>
      {/* AppBar for main menu */}
      <AppBar position="static" color="primary">
        <Toolbar>
          <img src={pantryLogo} alt="Pantry Logo" style={{ width: "40px", height: "40px", marginRight: "10px" }} />
          <Typography variant="h6" style={{ flexGrow: 1 }}>
            Smart Pantry Manager
          </Typography>
          <Button color="inherit" onClick={handleMenuOpen}>
            Menu
          </Button>
          {/* Dropdown menu */}
          <Menu
            anchorEl={menuAnchor}
            open={Boolean(menuAnchor)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={() => handleNavigation("Profile")}>Profile</MenuItem>
            <MenuItem onClick={() => handleNavigation("Shopping List")}>Shopping List</MenuItem>
            <MenuItem onClick={() => handleNavigation("Pantry Management")}>Pantry Management</MenuItem>
            <MenuItem onClick={() => handleNavigation("Recipe Suggestions")}>Recipe Suggestions</MenuItem>
          </Menu>
        </Toolbar>
      </AppBar>

      {/* Render the current page */}
      <Grid container spacing={3} style={{ marginTop: "20px" }}>
        <Grid item xs={12}>
          <Paper elevation={3} style={{ padding: "20px", minHeight: "300px" }}>
            {renderCurrentPage()}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
