import React, { useEffect, useState } from "react";
import { Typography, List, ListItem, ListItemText } from "@mui/material";

const RecipeSuggestions = ({ pantryItems }) => {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(false); // Add a loading state

  useEffect(() => {
    // If pantry items are provided, fetch new recipe suggestions
    if (pantryItems.length > 0) {
      setLoading(true); // Start loading
      console.log("Pantry items being sent to backend:", pantryItems);

      // Make a POST request to the Flask backend with the pantry items
      fetch("http://127.0.0.1:5001/recommend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ pantry: pantryItems }),
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          console.log("Received data from backend:", data);
          if (Array.isArray(data)) {
            setRecipes(data);
          } else {
            console.error("Received invalid data format");
            setRecipes([]);
          }
        })
        .catch((error) => {
          console.error("Error fetching recipe suggestions:", error);
          setRecipes([]); // Set to empty if an error occurs
        })
        .finally(() => {
          setLoading(false); // Stop loading
        });
    } else {
      // If there are no pantry items, clear the recipes
      setRecipes([]);
    }
  }, [pantryItems]); // Dependency on pantryItems, updates on change

  return (
    <div style={{ marginTop: "20px" }}>
      <Typography variant="h5" gutterBottom>
        Recipe Suggestions
      </Typography>
      {loading ? (
        <Typography>Loading...</Typography>
      ) : recipes.length > 0 ? (
        <List>
          {recipes.map((recipe, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={`Suggested Recipe: ${recipe.recipe_name}`}
                secondary={`Ingredients: ${recipe.ingredients}`}
              />
            </ListItem>
          ))}
        </List>
      ) : (
        <Typography>No recipe suggestions available.</Typography>
      )}
    </div>
  );
};

export default RecipeSuggestions;
