import React, { useEffect, useState, useRef } from "react";
import { Typography, List, ListItem, ListItemText, CircularProgress, Button } from "@mui/material";

const RecipeSuggestions = ({ pantryItems }) => {
  const [suggestions, setSuggestions] = useState("");
  const [loading, setLoading] = useState(false);
  const [lastPantryItems, setLastPantryItems] = useState([]); // Track last pantry items

  const isFirstRender = useRef(true); // To handle initial render

  useEffect(() => {
    if (isFirstRender.current) {
      isFirstRender.current = false;
      return; // Skip the first render to avoid unnecessary fetch
    }

    // Only fetch recipes if pantryItems are provided and different from the last fetched items
    if (pantryItems.length > 0 && JSON.stringify(pantryItems) !== JSON.stringify(lastPantryItems)) {
      setLastPantryItems(pantryItems); // Update last pantry items to prevent refetching the same items
      fetchSuggestions(pantryItems); // Call fetch function
    }
  }, [pantryItems]);

  const fetchSuggestions = (pantryItems) => {
    setLoading(true);
    console.log("Fetching suggestions with pantry items:", pantryItems);

    fetch("http://127.0.0.1:5001/recommend", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ pantry: pantryItems }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Received data:", data);  // Log the response
        if (data.suggestions) {
          setSuggestions(data.suggestions);
        } else {
          setSuggestions("No suggestions available.");
        }
      })
      .catch((error) => {
        console.error("Error fetching recipe suggestions:", error);
        setSuggestions("Error generating suggestions.");
      })
      .finally(() => {
        setLoading(false); // Stop loading after fetch completes
      });
  };


  return (
    <div style={{ marginTop: "20px" }}>
      <Typography variant="h5" gutterBottom>
        Recipe Suggestions from ChatGPT
      </Typography>

      {loading ? (
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          <CircularProgress />
        </div>
      ) : (
        <div>
          <Typography>{suggestions}</Typography>
        </div>
      )}
    </div>
  );
};

export default RecipeSuggestions;
