import React, { useState, useEffect } from "react";
import { Paper, TextField, Button, MenuItem, Typography, Grid, Select } from "@mui/material";
import "./Profile.css";

const Profile = ({ updateDietPreferences }) => {
  const [profileName, setProfileName] = useState("");
  const [availableProfiles, setAvailableProfiles] = useState([]);
  const [purpose, setPurpose] = useState("Single Person");
  const [familyMembers, setFamilyMembers] = useState(1);
  const [diet, setDiet] = useState("None");
  const [restrictions, setRestrictions] = useState("");
  const [cookDays, setCookDays] = useState(0);
  const [usualMeals, setUsualMeals] = useState("");
  const [pantryItems, setPantryItems] = useState([]);

  // Fetch available profiles on mount
  useEffect(() => {
    fetch('http://127.0.0.1:5001/list_profiles')
      .then(response => response.json())
      .then(data => {
        setAvailableProfiles(data.profiles || []);
      })
      .catch(error => console.error("Error fetching profiles:", error));
  }, []);

  // Fetch selected profile data
  const fetchProfileData = (name) => {
    fetch(`http://127.0.0.1:5001/get_profile/${name}`)
      .then(response => response.json())
      .then(data => {
        setPurpose(data.purpose || "Single Person");
        setFamilyMembers(data.familyMembers || 1);
        setCookDays(data.cookDays || 0);
        setDiet(data.diet || "None");
        setRestrictions(data.restrictions || "");
        setUsualMeals(data.usualMeals || "Regular");
        setProfileName(name);

        // Update diet preferences in the parent component when a profile is loaded
        updateDietPreferences({
          diet: data.diet || "None",
          restrictions: data.restrictions || "",
          usualMeals: data.usualMeals || "Regular",
        });
      })
      .catch(error => console.error("Error fetching profile:", error));
  };


  const handleProfileSubmit = () => {
    if (!profileName) {
      alert("Please enter a profile name before saving.");
      return;
    }

    const profileData = {
      purpose,
      familyMembers: purpose === "Family" ? familyMembers : 1,
      cookDays,
      diet,
      restrictions,
      usualMeals,
    };

    fetch('http://127.0.0.1:5001/save_profile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ profile_name: profileName, profile_data: profileData }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error("Failed to save profile.");
        }
        return response.json();
      })
      .then(data => {
        console.log("Profile saved:", data);
        alert("Profile saved successfully!");
        updateDietPreferences(diet, restrictions, usualMeals);
      })
      .catch(error => {
        console.error("Error saving profile:", error);
        alert("There was an error saving the profile. Please try again.");
      });
  };

  const handleRecommendation = () => {
    if (!profileName || pantryItems.length === 0) {
      alert("Please select a profile and add pantry items before getting recommendations.");
      return;
    }

    fetch('http://127.0.0.1:5001/recommend', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ profile_name: profileName, pantry: pantryItems.map(item => ({ name: item })) }),
    })
      .then(response => response.json())
      .then(data => {
        console.log("Received recipe suggestions:", data);
        alert("Check console for recipe suggestions.");
      })
      .catch(error => console.error("Error getting recipe suggestions:", error));
  };


  return (
    <Paper elevation={3} style={{ padding: "20px", marginBottom: "20px" }}>
      <Typography variant="h5" gutterBottom>
        Profile Settings
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Select
            value={profileName}
            onChange={(e) => fetchProfileData(e.target.value)}
            displayEmpty
            fullWidth
          >
            <MenuItem value="" disabled>Select a profile</MenuItem>
            {availableProfiles.map((name) => (
              <MenuItem key={name} value={name}>{name}</MenuItem>
            ))}
          </Select>
        </Grid>

        <Grid item xs={12}>
          <TextField
            label="Profile Name"
            value={profileName}
            onChange={(e) => setProfileName(e.target.value)}
            placeholder="Enter a unique profile name"
            fullWidth
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            select
            label="What's the app for?"
            value={purpose}
            onChange={(e) => setPurpose(e.target.value)}
            fullWidth
          >
            <MenuItem value="Single Person">Single Person</MenuItem>
            <MenuItem value="Couple">Couple</MenuItem>
            <MenuItem value="Family">Family</MenuItem>
          </TextField>
        </Grid>

        {purpose === "Family" && (
          <Grid item xs={12}>
            <TextField
              label="How many people in your family?"
              type="number"
              value={familyMembers}
              onChange={(e) => setFamilyMembers(e.target.value)}
              fullWidth
            />
          </Grid>
        )}

        <Grid item xs={12}>
          <TextField
            label="How many days per week do you cook at home?"
            type="number"
            value={cookDays}
            onChange={(e) => setCookDays(e.target.value)}
            fullWidth
          />
        </Grid>

        <Grid item xs={12}>
          <Typography variant="h6" gutterBottom>
            Diet Preferences
          </Typography>
          <TextField
            select
            label="Preferred Diet"
            value={diet}
            onChange={(e) => setDiet(e.target.value)}
            fullWidth
          >
            <MenuItem value="None">None</MenuItem>
            <MenuItem value="Vegan">Vegan</MenuItem>
            <MenuItem value="Keto">Keto</MenuItem>
            <MenuItem value="Low-Carb">Low-Carb</MenuItem>
          </TextField>
        </Grid>

        <Grid item xs={12}>
          <TextField
            label="Dietary Restrictions"
            value={restrictions}
            onChange={(e) => setRestrictions(e.target.value)}
            placeholder="e.g., No dairy, No gluten"
            fullWidth
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            select
            label="Usual Meals"
            value={usualMeals}
            onChange={(e) => setUsualMeals(e.target.value)}
            fullWidth
          >
            <MenuItem value="Regular">Regular</MenuItem>
            <MenuItem value="Salads">Salads</MenuItem>
            <MenuItem value="Steaks">Steaks</MenuItem>
            <MenuItem value="Persian">Persian</MenuItem>
          </TextField>
        </Grid>

        <Grid item xs={12}>
          <TextField
            label="Pantry Items"
            value={pantryItems.join(', ')}
            onChange={(e) => setPantryItems(e.target.value.split(',').map(item => item.trim()))}
            placeholder="Enter pantry items, separated by commas"
            fullWidth
          />
        </Grid>

        <Grid item xs={12}>
          <Button variant="contained" color="primary" onClick={handleProfileSubmit}>
            Save Profile
          </Button>
          <Button variant="contained" color="secondary" onClick={handleRecommendation} style={{ marginLeft: '10px' }}>
            Get Recipe Suggestions
          </Button>
        </Grid>
      </Grid>
    </Paper>
  );
};

export default Profile;
