import React, { useState } from "react";
import {
  Paper,
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Checkbox,
  ListItemText,
  Avatar,
} from "@mui/material";

const Ingredients = ({ pantryItems, onChange }) => {
  const [selectedItems, setSelectedItems] = useState([]);

  const handleSelectChange = (event) => {
    const updatedItems = event.target.value;
    setSelectedItems(updatedItems);
    onChange(updatedItems); // Notify the parent component of the selected items
  };

  return (
    <Paper elevation={3} style={{ padding: "20px" }}>
      <Typography variant="h5" gutterBottom>
        Select Ingredients
      </Typography>
      <FormControl fullWidth variant="outlined" style={{ marginTop: "20px", marginBottom: "20px" }}>
        <InputLabel id="select-pantry-items-label">Select Pantry Items</InputLabel>
        <Select
          labelId="select-pantry-items-label"
          multiple
          value={selectedItems}
          onChange={handleSelectChange}
          label="Select Pantry Items"
          renderValue={(selected) => selected.join(", ")}
        >
          {pantryItems.map((item) => (
            <MenuItem key={item.id} value={item.name} style={{ display: "flex", alignItems: "center" }}>
              <Checkbox checked={selectedItems.indexOf(item.name) > -1} />
              <Avatar
                src={item.image}
                alt={item.name}
                style={{ marginRight: "10px", width: "30px", height: "30px" }}
              />
              <ListItemText primary={item.name} />
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      <Typography variant="body1">
        {selectedItems.length > 0
          ? `Selected Ingredients: ${selectedItems.join(", ")}`
          : "No ingredients selected."}
      </Typography>
    </Paper>
  );
};

export default Ingredients;
