import React, { useEffect, useState, useRef } from "react";
import { Typography, CircularProgress, Button } from "@mui/material";

const RecipeSuggestions = ({ pantryItems, dietPreferences }) => {
  const [recipes, setRecipes] = useState([]); // Store all the recipes
  const [currentRecipeIndex, setCurrentRecipeIndex] = useState(0); // Track current recipe
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(""); // To store any error message

  useEffect(() => {
    if (pantryItems.length > 0) {
      fetchSuggestions(pantryItems, dietPreferences); // Fetch suggestions when pantry items or diet changes
    }
  }, [pantryItems, dietPreferences]);

  const fetchSuggestions = (pantryItems, dietPreferences) => {
    setLoading(true);
    setError(""); // Reset error on new fetch

    fetch("http://127.0.0.1:5001/recommend", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        pantry: pantryItems,
        diet: dietPreferences.diet,
        restrictions: dietPreferences.restrictions,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        if (data.suggestions && data.suggestions.length > 0) {
          setRecipes(data.suggestions);
          setCurrentRecipeIndex(0); // Reset to the first recipe when new data arrives
        } else {
          setError("No recipe suggestions available.");
        }
      })
      .catch((error) => {
        console.error("Error fetching recipe suggestions:", error);
        setError("Error generating suggestions.");
      })
      .finally(() => {
        setLoading(false); // Stop loading when done
      });
  };

  const handleDislike = () => {
    if (currentRecipeIndex < recipes.length - 1) {
      setCurrentRecipeIndex(currentRecipeIndex + 1); // Move to the next recipe
    } else {
      alert("No more recipes to show.");
    }
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <Typography variant="h5" gutterBottom>
        Recipe Suggestions from ChatGPT
      </Typography>

      {loading ? (
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          <CircularProgress />
        </div>
      ) : error ? (
        <Typography color="error">{error}</Typography>
      ) : recipes.length > 0 ? (
        <div>
          <Typography variant="h6" style={{ fontWeight: "bold", color: "#3f51b5" }}>
            {recipes[currentRecipeIndex].title} {/* Bold and styled title */}
          </Typography>
          <Typography variant="body1" style={{ whiteSpace: "pre-line", marginTop: "10px" }}>
            {recipes[currentRecipeIndex].instructions} {/* Instructions displayed normally */}
          </Typography>
          <div style={{ marginTop: "20px" }}>
            <Button variant="contained" color="primary" onClick={() => alert("You liked the recipe!")}>
              Like
            </Button>
            <Button variant="contained" color="secondary" onClick={handleDislike} style={{ marginLeft: "10px" }}>
              Dislike
            </Button>
          </div>
        </div>
      ) : (
        <Typography>No recipe suggestions available.</Typography>
      )}
    </div>
  );
};

export default RecipeSuggestions;
