import React, { useState, useEffect } from "react";
import { Typography, List, ListItem, ListItemText, Button } from "@mui/material";

const RecipeSuggestions = ({ pantryItems }) => {
  const [recipes, setRecipes] = useState([]);

  // Fetch recipes when pantryItems change
  useEffect(() => {
    if (pantryItems.length > 0) {
      fetchRecipes();
    }
  }, [pantryItems]);

  // Fetch recommended recipes from the Flask backend
  const fetchRecipes = async () => {
    const response = await fetch("http://127.0.0.1:5001/recommend", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ pantry: pantryItems }),
    });
    const data = await response.json();
    setRecipes(data);
  };

  return (
    <div className="recipe-suggestions">
      <Typography variant="h5" gutterBottom>
        Recipe Suggestions
      </Typography>
      {recipes.length > 0 ? (
        <List>
          {recipes.map((recipe, index) => (
            <ListItem key={index}>
              <ListItemText
                primary={recipe.recipe_name}
                secondary={`Ingredients: ${recipe.ingredients}`}
              />
            </ListItem>
          ))}
        </List>
      ) : (
        <Typography variant="body1">No recipe suggestions available.</Typography>
      )}
    </div>
  );
};

export default RecipeSuggestions;
