import React, { useState, useEffect } from "react";
import { Typography } from "@mui/material";
import AddItem from "./AddItem";
import ItemList from "./ItemList";
import RecipeSuggestions from "../RecipeSuggestions/RecipeSuggestions";
import "./Pantry.css";

const Pantry = () => {
  const [items, setItems] = useState([]); // Always start with an empty array

  // Fetch items from the backend when the component mounts
  useEffect(() => {
    console.log("Fetching pantry items from http://localhost:5000/pantry...");
    fetch("http://localhost:5000/pantry")
      .then((response) => response.json())
      .then((data) => {
        console.log("Fetched pantry items:", data); // Log the fetched data
        setItems(data || []); // Ensure data is always an array
      })
      .catch((error) => console.error("Error fetching pantry items:", error));
  }, []);

  // Add a new item to the pantry and update the backend
  const addItem = (item) => {
    console.log("Adding item to pantry:", item); // Log the item being added
    fetch("http://localhost:5000/pantry", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(item),
    })
      .then((response) => {
        console.log("Response from adding item:", response); // Log the response
        return response.json();
      })
      .then((newItem) => {
        console.log("New item added to pantry:", newItem); // Log the new item
        setItems([...items, newItem]); // Update state with the new item
      })
      .catch((error) => console.error("Error adding pantry item:", error));
  };

  // Extract only the item names for the recommendation engine
  const pantryItems = items.map((item) => item.name);

  console.log("Rendering Pantry component with items:", items);
  console.log("Passing pantryItems to RecipeSuggestions:", pantryItems);

  return (
    <div className="pantry-container">
      <Typography variant="h5" gutterBottom>
        Pantry
      </Typography>
      <AddItem addItem={addItem} />
      <ItemList items={items} />
      <RecipeSuggestions pantryItems={pantryItems} />
    </div>
  );
};

export default Pantry;
