import React, { useEffect, useState, useRef } from "react";
import { Typography, CircularProgress, Button } from "@mui/material";

const RecipeSuggestions = ({ pantryItems, dietPreferences }) => {
  const [recipes, setRecipes] = useState([]); // Store all the recipes
  const [currentRecipeIndex, setCurrentRecipeIndex] = useState(0); // Track current recipe
  const [loading, setLoading] = useState(false);
  const [previousPantryItems, setPreviousPantryItems] = useState([]); // Store previous pantry items to avoid refetching

  const isFirstRender = useRef(true); // To handle initial render

  useEffect(() => {
    if (JSON.stringify(pantryItems) === JSON.stringify(previousPantryItems)) return;

    if (pantryItems.length > 0) {
      setPreviousPantryItems(pantryItems);
      fetchSuggestions(pantryItems, dietPreferences);
    }
  }, [pantryItems, previousPantryItems, dietPreferences]);

  const fetchSuggestions = (pantryItems, dietPreferences) => {
    setLoading(true);
    console.log("Fetching suggestions with pantry items:", pantryItems, "and diet preferences:", dietPreferences);

    fetch("http://127.0.0.1:5001/recommend", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        pantry: pantryItems,
        diet: dietPreferences.diet,
        restrictions: dietPreferences.restrictions,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Received data:", data);
        if (data.suggestions && data.suggestions.length > 0) {
          setRecipes(data.suggestions);
          setCurrentRecipeIndex(0); // Reset to the first recipe only when new recipes are fetched
        } else {
          setRecipes(["No recipe suggestions available."]);
        }
      })
      .catch((error) => {
        console.error("Error fetching recipe suggestions:", error);
        setRecipes(["Error generating suggestions."]);
      })
      .finally(() => {
        setLoading(false); // Stop loading after fetch completes
      });
  };

  const handleLike = () => {
    alert("You liked the recipe!");
  };

  const handleDislike = () => {
    if (currentRecipeIndex < recipes.length - 1) {
      setCurrentRecipeIndex(currentRecipeIndex + 1); // Show the next recipe
    } else {
      alert("No more recipes to show.");
    }
  };

  // Function to split the recipe into title and instructions
  const getRecipeTitleAndInstructions = (recipe) => {
    const colonIndex = recipe.indexOf(":"); // Find the colon that separates the title from the instructions

    if (colonIndex !== -1) {
      const title = recipe.slice(0, colonIndex).trim(); // Extract the title
      const instructions = recipe.slice(colonIndex + 1).trim(); // Extract the instructions
      return { title, instructions };
    }
    // If no colon, treat the whole thing as instructions
    return { title: recipe, instructions: "" };
  };

  return (
    <div style={{ marginTop: "20px" }}>
      <Typography variant="h5" gutterBottom>
        Recipe Suggestions from ChatGPT
      </Typography>

      {loading ? (
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          <CircularProgress />
        </div>
      ) : recipes.length > 0 ? (
        <div>
          {/* Extract title and instructions from the current recipe */}
          {recipes[currentRecipeIndex] && (
            <>
              {(() => {
                const { title, instructions } = getRecipeTitleAndInstructions(
                  recipes[currentRecipeIndex]
                );
                return (
                  <>
                    <Typography variant="h6" style={{ fontWeight: "bold", color: "#3f51b5" }}>
                      {title} {/* Bold and styled title */}
                    </Typography>
                    <Typography variant="body1" style={{ whiteSpace: "pre-line", marginTop: "10px" }}>
                      {instructions} {/* Instructions displayed normally */}
                    </Typography>
                  </>
                );
              })()}
            </>
          )}
          <div style={{ marginTop: "20px" }}>
            <Button
              variant="contained"
              color="primary"
              style={{ marginRight: "10px" }}
              onClick={handleLike}
            >
              Like
            </Button>
            <Button
              variant="contained"
              color="secondary"
              onClick={handleDislike}
            >
              Dislike
            </Button>
          </div>
        </div>
      ) : (
        <Typography>No recipe suggestions available.</Typography>
      )}
    </div>
  );
};

export default RecipeSuggestions;
