import React, { useState, useEffect } from "react";
import {
  Paper,
  TextField,
  Button,
  MenuItem,
  Typography,
  Grid,
  Select,
  FormControl,
  InputLabel,
} from "@mui/material";
import "./Profile.css";

const Profile = ({ updateDietPreferences }) => {
  const [profileName, setProfileName] = useState("");
  const [profiles, setProfiles] = useState([]);
  const [selectedProfile, setSelectedProfile] = useState("");
  const [purpose, setPurpose] = useState("Single Person");
  const [familyMembers, setFamilyMembers] = useState(1);
  const [diet, setDiet] = useState("None");
  const [restrictions, setRestrictions] = useState("");
  const [cookDays, setCookDays] = useState(0);
  const [usualMeals, setUsualMeals] = useState("");

  useEffect(() => {
    fetch("http://127.0.0.1:5001/get_profile")
      .then((response) => response.json())
      .then((data) => {
        setProfiles(data.profiles || []);
        console.log("Fetched profile data:", data);
      })
      .catch((error) => console.error("Error fetching profile:", error));
  }, []);

  const handleProfileSubmit = () => {
    if (!profileName.trim()) {
      alert("Please enter a profile name.");
      return;
    }

    const profileData = {
      name: profileName,
      purpose,
      familyMembers: purpose === "Family" ? familyMembers : 1,
      cookDays,
      diet,
      restrictions,
      usualMeals,
    };

    fetch("http://127.0.0.1:5001/save_profile", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(profileData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Profile saved:", data);
        updateDietPreferences({
          profileName: profileData.name,
          diet: profileData.diet,
          restrictions: profileData.restrictions,
          usualMeals: profileData.usualMeals,
        });

        if (!profiles.find((p) => p.name === profileData.name)) {
          setProfiles((prev) => [...prev, profileData]);
        }
      })
      .catch((error) => console.error("Error saving profile:", error));
  };

  const handleProfileSelect = (event) => {
    setSelectedProfile(event.target.value);
  };

  const handleLoadProfile = () => {
    const profile = profiles.find((p) => p.name === selectedProfile);
    if (profile) {
      setProfileName(profile.name);
      setPurpose(profile.purpose);
      setFamilyMembers(profile.familyMembers);
      setCookDays(profile.cookDays);
      setDiet(profile.diet);
      setRestrictions(profile.restrictions);
      setUsualMeals(profile.usualMeals);

      updateDietPreferences({
        profileName: profile.name,
        diet: profile.diet,
        restrictions: profile.restrictions,
        usualMeals: profile.usualMeals,
      });

      console.log("Loaded profile:", profile);
    }
  };

  return (
    <Paper elevation={3} style={{ padding: "20px", marginBottom: "20px" }}>
      <Typography variant="h5" gutterBottom>
        Profile Settings
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <TextField
            label="Profile Name"
            value={profileName}
            onChange={(e) => setProfileName(e.target.value)}
            placeholder="Enter a name for your profile"
            fullWidth
          />
        </Grid>

        <Grid item xs={12}>
          <FormControl fullWidth>
            <InputLabel>Select Profile</InputLabel>
            <Select value={selectedProfile} onChange={handleProfileSelect} fullWidth>
              {profiles.length > 0 ? (
                profiles.map((profile) => (
                  <MenuItem key={profile.name} value={profile.name}>
                    {profile.name}
                  </MenuItem>
                ))
              ) : (
                <MenuItem disabled>No profiles available</MenuItem>
              )}
            </Select>
          </FormControl>
        </Grid>

        <Grid item xs={12}>
          <Button variant="contained" color="primary" onClick={handleLoadProfile} disabled={!selectedProfile}>
            Load Profile
          </Button>
        </Grid>

        <Grid item xs={12}>
          <TextField
            select
            label="What's the app for?"
            value={purpose}
            onChange={(e) => setPurpose(e.target.value)}
            fullWidth
          >
            <MenuItem value="Single Person">Single Person</MenuItem>
            <MenuItem value="Couple">Couple</MenuItem>
            <MenuItem value="Family">Family</MenuItem>
          </TextField>
        </Grid>

        {purpose === "Family" && (
          <Grid item xs={12}>
            <TextField
              label="How many people in your family?"
              type="number"
              value={familyMembers}
              onChange={(e) => setFamilyMembers(e.target.value)}
              fullWidth
            />
          </Grid>
        )}

        <Grid item xs={12}>
          <TextField
            label="How many days per week do you cook at home?"
            type="number"
            value={cookDays}
            onChange={(e) => setCookDays(e.target.value)}
            fullWidth
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            select
            label="Preferred Diet"
            value={diet}
            onChange={(e) => setDiet(e.target.value)}
            fullWidth
          >
            <MenuItem value="None">None</MenuItem>
            <MenuItem value="Vegan">Vegan</MenuItem>
            <MenuItem value="Keto">Keto</MenuItem>
            <MenuItem value="Low-Carb">Low-Carb</MenuItem>
          </TextField>
        </Grid>

        <Grid item xs={12}>
          <TextField
            label="Dietary Restrictions"
            value={restrictions}
            onChange={(e) => setRestrictions(e.target.value)}
            placeholder="e.g., No dairy, No gluten"
            fullWidth
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            select
            label="What are your usual meals?"
            value={usualMeals}
            onChange={(e) => setUsualMeals(e.target.value)}
            fullWidth
          >
            <MenuItem value="Steaks">Steaks</MenuItem>
            <MenuItem value="Salads">Salads</MenuItem>
            <MenuItem value="Regular">Regular</MenuItem>
            <MenuItem value="Persian">Persian</MenuItem>
            <MenuItem value="Chinese">Chinese</MenuItem>
            <MenuItem value="Fish and Seafood">Fish and Seafood</MenuItem>
          </TextField>
        </Grid>

        <Grid item xs={12}>
          <Button variant="contained" color="primary" onClick={handleProfileSubmit}>
            Save Profile
          </Button>
        </Grid>
      </Grid>
    </Paper>
  );
};

export default Profile;
