import React, { useState } from "react";
import { Button, TextField, List, ListItem, ListItemText, IconButton } from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";

const Pantry = ({ updatePantryItems }) => {
  const [pantryInput, setPantryInput] = useState("");
  const [pantryList, setPantryList] = useState([]);

  // Handle adding items to pantry
  const handleAddItem = () => {
    if (pantryInput.trim() !== "") {
      const newPantryList = [...pantryList, pantryInput.trim()];
      setPantryList(newPantryList);
      updatePantryItems(newPantryList); // Update the parent component with new pantry items
      setPantryInput(""); // Clear the input field
    }
  };

  // Handle removing items from pantry
  const handleRemoveItem = (itemToRemove) => {
    const newPantryList = pantryList.filter((item) => item !== itemToRemove);
    setPantryList(newPantryList);
    updatePantryItems(newPantryList); // Update the parent component with new pantry items
  };

  return (
    <div>
      <TextField
        label="Add item to pantry"
        value={pantryInput}
        onChange={(e) => setPantryInput(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && handleAddItem()}
        style={{ marginRight: "10px" }}
      />
      <Button variant="contained" onClick={handleAddItem}>
        ADD
      </Button>

      <h3>Current Pantry Items</h3>
      <List>
        {pantryList.map((item, index) => (
          <ListItem key={index} secondaryAction={
            <IconButton edge="end" aria-label="delete" onClick={() => handleRemoveItem(item)}>
              <DeleteIcon />
            </IconButton>
          }>
            <ListItemText primary={item} />
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default Pantry;
