import React, { useState } from "react";
import { Container, Typography, Grid, Paper } from "@mui/material";
import Pantry from "./components/Pantry/Pantry";
import DietPreferences from "./components/DietPreferences/DietPreferences";
import RecipeSuggestions from "./components/RecipeSuggestions/RecipeSuggestions";
import ShoppingList from "./components/ShoppingList/ShoppingList";
import Profile from "./components/Profile/Profile";
import "./App.css";
import pantryLogo from "./assets/pantry-logo.png"; 

function App() {
  const [pantryItems, setPantryItems] = useState([]);
  const [dietPreferences, setDietPreferences] = useState({ diet: "None", restrictions: "" });
  const [profile, setProfile] = useState({ purpose: "Single", familyMembers: 1, diet: "None", cookDays: 0 });

  const updatePantryItems = (items) => {
    setPantryItems(items);
  };

  const updateDietPreferences = (diet, restrictions) => {
    setDietPreferences({ diet, restrictions });
  };

  const updateProfile = (profileData) => {
    setProfile(profileData);
    console.log("Profile updated:", profileData);
  };

  return (
    <Container maxWidth="lg" style={{ marginTop: "40px" }}>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "20px",
        }}
      >
        <img
          src={pantryLogo}
          alt="Pantry Logo"
          style={{ width: "60px", height: "60px", marginRight: "10px" }}
        />
        <Typography
          variant="h3"
          gutterBottom
          style={{
            fontFamily: "'Pacifico', cursive",
            color: "#4A4A4A",
            fontSize: "3rem",
          }}
        >
          Smart Pantry Manager
        </Typography>
      </div>

      <Grid container spacing={3}>
        {/* Left Column */}
        <Grid item xs={12} md={6}>
          <Profile updateProfile={updateProfile} />

          <Paper elevation={3} style={{ padding: "20px", marginBottom: "20px", minHeight: "270px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Diet Preferences
            </Typography>
            <DietPreferences updateDietPreferences={updateDietPreferences} />
          </Paper>

          <Paper elevation={3} style={{ padding: "20px", minHeight: "300px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Pantry Management
            </Typography>
            <Pantry updatePantryItems={updatePantryItems} />
          </Paper>
        </Grid>

        {/* Right Column */}
        <Grid item xs={12} md={6}>
          <Paper elevation={3} style={{ padding: "20px", marginBottom: "20px", minHeight: "270px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Recipe Suggestions
            </Typography>
            <RecipeSuggestions pantryItems={pantryItems} dietPreferences={dietPreferences} />
          </Paper>

          <Paper elevation={3} style={{ padding: "20px", minHeight: "300px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Shopping List
            </Typography>
            <ShoppingList />
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
