import React, { useState } from "react";
import { Container, Typography, Grid, Paper } from "@mui/material";
import Pantry from "./components/Pantry/Pantry";
import Profile from "./components/Profile/Profile"; // Updated Profile component
import RecipeSuggestions from "./components/RecipeSuggestions/RecipeSuggestions";
import ShoppingList from "./components/ShoppingList/ShoppingList";
import "./App.css"; // Make sure the custom CSS file is linked properly
import pantryLogo from "./assets/pantry-logo.png"; // Import the logo image

function App() {
  const [pantryItems, setPantryItems] = useState([]);
  const [dietPreferences, setDietPreferences] = useState({
    diet: "None",
    restrictions: "",
  });
  const [profile, setProfile] = useState({
    purpose: "Single",
    familyMembers: 1,
    cookDays: 0,
  });

  const updatePantryItems = (items) => {
    setPantryItems(items);
  };

  const updateDietPreferences = (diet, restrictions) => {
    setDietPreferences({ diet, restrictions });
  };

  const updateProfile = (profileData) => {
    setProfile(profileData);
  };

  return (
    <Container maxWidth="lg" style={{ marginTop: "40px" }}>
      {/* Custom Logo and Title Section */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "20px",
          gap: "10px", // Add space between logo and text
        }}
      >
        <img
          src={pantryLogo}
          alt="Pantry Logo"
          style={{
            width: "70px", // Increase logo size
            height: "70px",
            marginRight: "10px",
          }}
        />
        <Typography
          variant="h3"
          gutterBottom
          style={{
            fontFamily: "Roboto, sans-serif", // Simple, modern font
            color: "#333", // Dark gray for a modern look
            fontSize: "2.8rem", // Adjusted size for better balance with the logo
            fontWeight: "bold",
            textShadow: "1px 1px 2px rgba(0, 0, 0, 0.1)", // Adds subtle shadow for depth
          }}
        >
          Smart Pantry Manager
        </Typography>
      </div>

      {/* The rest of your app */}
      <Grid container spacing={3}>
        {/* Left Column */}
        <Grid item xs={12} md={6}>
          <Profile
            updateProfile={updateProfile}
            updateDietPreferences={updateDietPreferences}
          />

          <Paper elevation={3} style={{ padding: "20px", minHeight: "300px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Pantry Management
            </Typography>
            <Pantry updatePantryItems={updatePantryItems} />
          </Paper>
        </Grid>

        {/* Right Column */}
        <Grid item xs={12} md={6}>
          <Paper elevation={3} style={{ padding: "20px", marginBottom: "20px", minHeight: "270px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Recipe Suggestions
            </Typography>
            <RecipeSuggestions
              pantryItems={pantryItems}
              dietPreferences={dietPreferences}
              profile={profile} // Pass the profile data to RecipeSuggestions
            />
          </Paper>

          <Paper elevation={3} style={{ padding: "20px", minHeight: "300px" }}>
            <Typography variant="h5" align="left" gutterBottom>
              Shopping List
            </Typography>
            <ShoppingList />
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
